import React, { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from 'react-query';
import { usersApi } from '../services/api';

interface User {
  id: string;
  email: string;
  joinDate: string;
  coins: number;
  streak: number;
  status: 'active' | 'blocked';
}

export default function Users() {
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [selectedUser, setSelectedUser] = useState<User | null>(null);
  const [showPreview, setShowPreview] = useState(false);
  const queryClient = useQueryClient();

  const { data: usersData, isLoading, error } = useQuery(
    ['users', currentPage, searchTerm],
    () => usersApi.getUsers(currentPage, 20),
    {
      staleTime: 30000,
    }
  );

  const blockUserMutation = useMutation(
    (userId: string) => usersApi.blockUser(userId),
    {
      onSuccess: () => {
        queryClient.invalidateQueries(['users']);
      },
    }
  );

  const unblockUserMutation = useMutation(
    (userId: string) => usersApi.unblockUser(userId),
    {
      onSuccess: () => {
        queryClient.invalidateQueries(['users']);
      },
    }
  );

  const handleBlockUser = (userId: string) => {
    blockUserMutation.mutate(userId);
  };

  const handleUnblockUser = (userId: string) => {
    unblockUserMutation.mutate(userId);
  };

  const handleViewUser = (user: User) => {
    setSelectedUser(user);
    setShowPreview(true);
  };

  const handleClosePreview = () => {
    setShowPreview(false);
    setSelectedUser(null);
  };

  const handleExportUsers = () => {
    if (!users.length) return;

    // Create CSV content
    const headers = ['Email', 'User ID', 'Join Date', 'Status', 'Coins', 'Streak'];
    const csvContent = [
      headers.join(','),
      ...users.map(user => [
        user.email,
        user.id,
        new Date(user.joinDate).toLocaleDateString(),
        user.status,
        user.coins.toString(),
        user.streak.toString()
      ].join(','))
    ].join('\n');

    // Create and download file
    const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
    const link = document.createElement('a');
    const url = URL.createObjectURL(blob);
    link.setAttribute('href', url);
    link.setAttribute('download', `users_export_${new Date().toISOString().split('T')[0]}.csv`);
    link.style.visibility = 'hidden';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  if (isLoading) {
    return (
      <div className="space-y-6">
        <div className="fade-in">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">User Management</h1>
          <p className="text-gray-600">Manage user accounts and monitor user activity</p>
        </div>
        <div className="flex items-center justify-center h-64">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-purple-600"></div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="space-y-6">
        <div className="fade-in">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">User Management</h1>
          <p className="text-gray-600">Manage user accounts and monitor user activity</p>
        </div>
        <div className="bg-red-50 border border-red-200 rounded-xl p-6">
          <div className="flex">
            <div className="ml-3">
              <h3 className="text-sm font-medium text-red-800">
                Error loading users
              </h3>
              <div className="mt-2 text-sm text-red-700">
                Please try refreshing the page.
              </div>
            </div>
          </div>
        </div>
      </div>
    );
