import React, { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from 'react-query';
import { withdrawalsApi } from '../services/api';

export default function Withdrawals() {
  const [page, setPage] = useState(1);
  const [rejectReason, setRejectReason] = useState('');
  const [selectedWithdrawal, setSelectedWithdrawal] = useState<string | null>(null);
  const queryClient = useQueryClient();

  const { data, isLoading, error } = useQuery(
    ['withdrawals', page],
    () => withdrawalsApi.getWithdrawals(page, 20),
    {
      keepPreviousData: true,
    }
  );

  const approveMutation = useMutation(withdrawalsApi.approveWithdrawal, {
    onSuccess: () => {
      queryClient.invalidateQueries(['withdrawals']);
    },
  });

  const rejectMutation = useMutation(
    (withdrawalId: string) => withdrawalsApi.rejectWithdrawal(withdrawalId, rejectReason),
    {
      onSuccess: () => {
        queryClient.invalidateQueries(['withdrawals']);
        setRejectReason('');
        setSelectedWithdrawal(null);
      },
    }
  );

  const handleApprove = (withdrawalId: string) => {
    if (window.confirm('Are you sure you want to approve this withdrawal?')) {
      approveMutation.mutate(withdrawalId);
    }
  };

  const handleReject = (withdrawalId: string) => {
    if (!rejectReason.trim()) {
      alert('Please provide a reason for rejection');
      return;
    }
    rejectMutation.mutate(withdrawalId);
  };

  // Mock data for demo
  const mockWithdrawals = {
    withdrawals: [
      {
        id: '1',
        userId: 'user1',
        userEmail: 'john@example.com',
        amount: 2500,
        netAmount: 2375,
        fee: 125,
        method: 'mobile_money',
        status: 'pending',
        notes: 'Please process quickly',
        createdAt: '2024-01-20T10:30:00Z',
      },
      {
        id: '2',
        userId: 'user2',
        userEmail: 'sarah@example.com',
        amount: 1500,
        netAmount: 1425,
        fee: 75,
        method: 'bank_transfer',
        status: 'approved',
        notes: '',
        createdAt: '2024-01-19T14:22:00Z',
        approvedAt: '2024-01-20T09:15:00Z',
      },
      {
        id: '3',
        userId: 'user3',
        userEmail: 'mike@example.com',
        amount: 3000,
        netAmount: 2850,
        fee: 150,
        method: 'paypal',
        status: 'rejected',
        notes: 'Invalid PayPal email',
        createdAt: '2024-01-18T16:45:00Z',
        rejectedAt: '2024-01-19T11:30:00Z',
        rejectionReason: 'Invalid PayPal email address',
      },
    ],
    pagination: {
      page: 1,
      limit: 20,
      total: 89,
      pages: 5,
    },
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case 'pending':
        return (
          <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-yellow-100 text-yellow-800">
            Pending
          </span>
        );
      case 'approved':
        return (
          <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800">
            Approved
          </span>
        );
      case 'rejected':
        return (
          <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-red-100 text-red-800">
            Rejected
          </span>
        );
      default:
        return null;
    }
  };

  return (
    <div className="space-y-6">
      <div className="sm:flex sm:items-center sm:justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Withdrawals</h1>
          <p className="mt-1 text-sm text-gray-500">
            Manage withdrawal requests and approvals
          </p>
        </div>
      </div>

      {/* Withdrawals Table */}
      <div className="bg-white shadow overflow-hidden sm:rounded-md">
        <ul className="divide-y divide-gray-200">
          {mockWithdrawals.withdrawals.map((withdrawal) => (
            <li key={withdrawal.id}>
              <div className="px-4 py-4 sm:px-6">
                <div className="flex items-center justify-between">
                  <div className="flex items-center">
                    <div className="flex-shrink-0">
                      <div className="h-10 w-10 bg-primary-100 rounded-full flex items-center justify-center">
                        <span className="text-primary-600 font-medium">
                          {withdrawal.userEmail.charAt(0).toUpperCase()}
                        </span>
                      </div>
                    </div>
                    <div className="ml-4">
                      <div className="text-sm font-medium text-gray-900">
                        {withdrawal.userEmail}
                      </div>
                      <div className="text-sm text-gray-500">
                        {new Date(withdrawal.createdAt).toLocaleDateString()}
                      </div>
                    </div>
                  </div>
                  <div className="flex items-center space-x-4">
                    <div className="text-sm text-gray-500">
                      <div>Amount: ${withdrawal.amount.toLocaleString()}</div>
                      <div>Net: ${withdrawal.netAmount.toLocaleString()}</div>
                      <div>Fee: ${withdrawal.fee.toLocaleString()}</div>
                      <div>Method: {withdrawal.method.replace('_', ' ')}</div>
                    </div>
                    <div className="flex items-center space-x-2">
                      {getStatusBadge(withdrawal.status)}
                    </div>
                    <div className="flex items-center space-x-2">
                      {withdrawal.status === 'pending' && (
                        <>
                          <button
                            onClick={() => handleApprove(withdrawal.id)}
                            className="inline-flex items-center px-3 py-1 border border-transparent text-sm leading-4 font-medium rounded-md text-white bg-success-600 hover:bg-success-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-success-500"
                          >
                            Approve
                          </button>
                          <button
                            onClick={() => setSelectedWithdrawal(withdrawal.id)}
                            className="inline-flex items-center px-3 py-1 border border-transparent text-sm leading-4 font-medium rounded-md text-white bg-danger-600 hover:bg-danger-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-danger-500"
                          >
                            Reject
                          </button>
                        </>
                      )}
                    </div>
                  </div>
                </div>
                {withdrawal.notes && (
                  <div className="mt-2 text-sm text-gray-500">
                    <strong>Notes:</strong> {withdrawal.notes}
                  </div>
                )}
                {withdrawal.rejectionReason && (
                  <div className="mt-2 text-sm text-red-500">
                    <strong>Rejection Reason:</strong> {withdrawal.rejectionReason}
                  </div>
                )}
              </div>
            </li>
          ))}
        </ul>
      </div>

      {/* Reject Modal */}
      {selectedWithdrawal && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
          <div className="relative top-20 mx-auto p-5 border w-96 shadow-lg rounded-md bg-white">
            <div className="mt-3">
              <h3 className="text-lg font-medium text-gray-900 mb-4">
                Reject Withdrawal
              </h3>
              <textarea
                value={rejectReason}
                onChange={(e) => setRejectReason(e.target.value)}
                placeholder="Enter reason for rejection..."
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-1 focus:ring-primary-500 focus:border-primary-500"
                rows={3}
              />
              <div className="flex justify-end space-x-3 mt-4">
                <button
                  onClick={() => {
                    setSelectedWithdrawal(null);
                    setRejectReason('');
                  }}
                  className="px-4 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 hover:bg-gray-50"
                >
                  Cancel
                </button>
                <button
                  onClick={() => handleReject(selectedWithdrawal)}
                  className="px-4 py-2 border border-transparent rounded-md text-sm font-medium text-white bg-danger-600 hover:bg-danger-700"
                >
                  Reject
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Pagination */}
      <div className="bg-white px-4 py-3 flex items-center justify-between border-t border-gray-200 sm:px-6">
        <div className="flex-1 flex justify-between sm:hidden">
          <button
            onClick={() => setPage(Math.max(1, page - 1))}
            disabled={page === 1}
            className="relative inline-flex items-center px-4 py-2 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
          >
            Previous
          </button>
          <button
            onClick={() => setPage(page + 1)}
            disabled={page >= mockWithdrawals.pagination.pages}
            className="ml-3 relative inline-flex items-center px-4 py-2 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
          >
            Next
          </button>
        </div>
        <div className="hidden sm:flex-1 sm:flex sm:items-center sm:justify-between">
          <div>
            <p className="text-sm text-gray-700">
              Showing{' '}
              <span className="font-medium">{(page - 1) * 20 + 1}</span>
              {' '}to{' '}
              <span className="font-medium">
                {Math.min(page * 20, mockWithdrawals.pagination.total)}
              </span>
              {' '}of{' '}
              <span className="font-medium">{mockWithdrawals.pagination.total}</span>
              {' '}results
            </p>
          </div>
          <div>
            <nav className="relative z-0 inline-flex rounded-md shadow-sm -space-x-px">
              <button
                onClick={() => setPage(Math.max(1, page - 1))}
                disabled={page === 1}
                className="relative inline-flex items-center px-2 py-2 rounded-l-md border border-gray-300 bg-white text-sm font-medium text-gray-500 hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
              >
                Previous
              </button>
              <button
                onClick={() => setPage(page + 1)}
                disabled={page >= mockWithdrawals.pagination.pages}
                className="relative inline-flex items-center px-2 py-2 rounded-r-md border border-gray-300 bg-white text-sm font-medium text-gray-500 hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
              >
                Next
              </button>
            </nav>
          </div>
        </div>
      </div>
    </div>
  );
}
