import React from 'react';
import { useQuery } from 'react-query';
import { analyticsApi } from '../services/api';
import { format } from 'date-fns';

interface StatCardProps {
  title: string;
  value: string | number;
  change?: string;
  changeType?: 'positive' | 'negative' | 'neutral';
  icon: string;
}

function StatCard({ title, value, change, changeType = 'neutral', icon }: StatCardProps) {
  return (
    <div className="bg-white overflow-hidden shadow rounded-lg">
      <div className="p-5">
        <div className="flex items-center">
          <div className="flex-shrink-0">
            <div className="w-8 h-8 bg-primary-100 rounded-md flex items-center justify-center">
              <span className="text-primary-600 text-lg">{icon}</span>
            </div>
          </div>
          <div className="ml-5 w-0 flex-1">
            <dl>
              <dt className="text-sm font-medium text-gray-500 truncate">{title}</dt>
              <dd className="flex items-baseline">
                <div className="text-2xl font-semibold text-gray-900">{value}</div>
                {change && (
                  <div className={`ml-2 flex items-baseline text-sm font-semibold ${
                    changeType === 'positive' ? 'text-success-600' :
                    changeType === 'negative' ? 'text-danger-600' : 'text-gray-500'
                  }`}>
                    {change}
                  </div>
                )}
              </dd>
            </dl>
          </div>
        </div>
      </div>
    </div>
  );
}

export default function Dashboard() {
  const { data: stats, isLoading, error } = useQuery(
    'dashboard-stats',
    analyticsApi.getDashboardStats,
    {
      refetchInterval: 30000, // Refetch every 30 seconds
    }
  );

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-red-50 border border-red-200 rounded-md p-4">
        <div className="flex">
          <div className="ml-3">
            <h3 className="text-sm font-medium text-red-800">
              Error loading dashboard data
            </h3>
            <div className="mt-2 text-sm text-red-700">
              Please try refreshing the page.
            </div>
          </div>
        </div>
      </div>
    );
  }

  // Mock data for demo - replace with real data from API
  const mockStats = {
    totalUsers: 1250,
    activeUsers: 342,
    totalSpins: 15420,
    totalWithdrawals: 89,
    totalRevenue: 1250.50,
    avgSpinsPerUser: 12.3,
    conversionRate: 7.1,
    avgWithdrawalAmount: 14.05,
  };

  return (
    <div className="space-y-6">
      <div>
        <h1 className="text-2xl font-bold text-gray-900">Dashboard</h1>
        <p className="mt-1 text-sm text-gray-500">
          Overview of your Spin & Earn platform
        </p>
      </div>

      {/* Stats Grid */}
      <div className="grid grid-cols-1 gap-5 sm:grid-cols-2 lg:grid-cols-4">
        <StatCard
          title="Total Users"
          value={mockStats.totalUsers.toLocaleString()}
          change="+12%"
          changeType="positive"
          icon="ðŸ‘¥"
        />
        <StatCard
          title="Active Users (24h)"
          value={mockStats.activeUsers.toLocaleString()}
          change="+5%"
          changeType="positive"
          icon="ðŸ”¥"
        />
        <StatCard
          title="Total Spins"
          value={mockStats.totalSpins.toLocaleString()}
          change="+8%"
          changeType="positive"
          icon="ðŸŽ°"
        />
        <StatCard
          title="Total Withdrawals"
          value={mockStats.totalWithdrawals.toLocaleString()}
          change="+3%"
          changeType="positive"
          icon="ðŸ’°"
        />
      </div>

      {/* Additional Stats */}
      <div className="grid grid-cols-1 gap-5 sm:grid-cols-2 lg:grid-cols-4">
        <StatCard
          title="Revenue (USD)"
          value={`$${mockStats.totalRevenue.toLocaleString()}`}
          change="+15%"
          changeType="positive"
          icon="ðŸ’µ"
        />
        <StatCard
          title="Avg Spins/User"
          value={mockStats.avgSpinsPerUser}
          change="+2%"
          changeType="positive"
          icon="ðŸ“Š"
        />
        <StatCard
          title="Conversion Rate"
          value={`${mockStats.conversionRate}%`}
          change="+1%"
          changeType="positive"
          icon="ðŸ“ˆ"
        />
        <StatCard
          title="Avg Withdrawal"
          value={`$${mockStats.avgWithdrawalAmount}`}
          change="+5%"
          changeType="positive"
          icon="ðŸ’³"
        />
      </div>

      {/* Recent Activity */}
      <div className="bg-white shadow rounded-lg">
        <div className="px-4 py-5 sm:p-6">
          <h3 className="text-lg leading-6 font-medium text-gray-900">
            Recent Activity
          </h3>
          <div className="mt-4 flow-root">
            <ul className="-mb-8">
              {[
                { id: 1, user: 'john@example.com', action: 'Completed withdrawal', amount: '$25.00', time: '2 minutes ago' },
                { id: 2, user: 'sarah@example.com', action: 'Won jackpot', amount: '500 coins', time: '5 minutes ago' },
                { id: 3, user: 'mike@example.com', action: 'Registered', amount: '', time: '10 minutes ago' },
                { id: 4, user: 'lisa@example.com', action: 'Completed streak', amount: '50 coins', time: '15 minutes ago' },
              ].map((activity, activityIdx) => (
                <li key={activity.id}>
                  <div className="relative pb-8">
                    {activityIdx !== 3 ? (
                      <span
                        className="absolute top-4 left-4 -ml-px h-full w-0.5 bg-gray-200"
                        aria-hidden="true"
                      />
                    ) : null}
                    <div className="relative flex space-x-3">
                      <div>
                        <span className="h-8 w-8 rounded-full bg-primary-500 flex items-center justify-center ring-8 ring-white">
                          <span className="text-white text-sm">ðŸŽ¯</span>
                        </span>
                      </div>
                      <div className="min-w-0 flex-1 pt-1.5 flex justify-between space-x-4">
                        <div>
                          <p className="text-sm text-gray-500">
                            <span className="font-medium text-gray-900">{activity.user}</span> {activity.action}
                            {activity.amount && <span className="font-medium text-gray-900"> {activity.amount}</span>}
                          </p>
                        </div>
                        <div className="text-right text-sm whitespace-nowrap text-gray-500">
                          <time>{activity.time}</time>
                        </div>
                      </div>
                    </div>
                  </div>
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
}
